import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Input } from "./ui/input";
import { Textarea } from "./ui/textarea";
import { Badge } from "./ui/badge";

const WeeklyPriorities = ({ priorities, setPriorities, tasks }) => {
  const handlePriorityChange = (id, field, value) => {
    setPriorities(priorities =>
      priorities.map(p => p.id === id ? { ...p, [field]: value } : p)
    );
  };

  const getTasksForPriority = (priorityId) => {
    return Object.entries(tasks).flatMap(([date, dailyTasks]) => 
      dailyTasks.filter(task => task.priority === priorityId.toString())
        .map(task => ({ ...task, date }))
    );
  };

  return (
    <Card className="shadow-lg">
      <CardHeader className="bg-gradient-to-r from-blue-500 to-purple-500 text-white">
        <CardTitle className="text-2xl font-bold">Weekly Priorities</CardTitle>
      </CardHeader>
      <CardContent className="mt-4">
        {priorities.map((priority) => (
          <div key={priority.id} className="mb-6">
            <Badge className={`mb-2 ${priority.type === 'top' ? 'bg-yellow-500' : 'bg-blue-500'}`}>
              {priority.type === 'top' ? 'Top Priority' : 'Secondary Priority'}
            </Badge>
            <Input
              placeholder={`Enter ${priority.type === 'top' ? 'top' : 'secondary'} priority`}
              value={priority.title}
              onChange={(e) => handlePriorityChange(priority.id, 'title', e.target.value)}
              className="mb-2"
            />
            <Textarea
              placeholder="Describe successful outcome"
              value={priority.outcome}
              onChange={(e) => handlePriorityChange(priority.id, 'outcome', e.target.value)}
              className="h-24 mb-2"
            />
            <div className="mt-2">
              <h4 className="font-semibold">Related Tasks:</h4>
              <ul className="list-disc pl-5">
                {getTasksForPriority(priority.id).map(task => (
                  <li key={task.id}>
                    <Link to={`/daily?date=${task.date}&taskId=${task.id}`} className="text-blue-500 hover:underline">
                      {task.title} ({task.date})
                    </Link>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </CardContent>
    </Card>
  );
};

export default WeeklyPriorities;
