import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { PlusCircle, CheckCircle, Clock, GripVertical, Trash2 } from 'lucide-react';

const getCurrentDate = () => new Date().toISOString().split('T')[0];

const DailyTasks = ({ tasks, setTasks, priorities }) => {
  const [selectedDate, setSelectedDate] = useState(getCurrentDate());
  const [newTask, setNewTask] = useState({ title: '', estimatedPomodoros: 0, actualPomodoros: 0, status: 'pending', priority: '' });

  useEffect(() => {
    console.log('Current tasks:', tasks);
    console.log('Selected date:', selectedDate);
    console.log('Tasks for selected date:', tasks[selectedDate]);
  }, [tasks, selectedDate]);

  const addTask = () => {
    if (newTask.title) {
      setTasks(prevTasks => ({
        ...prevTasks,
        [selectedDate]: [...(prevTasks[selectedDate] || []), { ...newTask, id: Date.now().toString() }]
      }));
      setNewTask({ title: '', estimatedPomodoros: 0, actualPomodoros: 0, status: 'pending', priority: '' });
    }
  };

  const onDragEnd = (result) => {
    console.log('Drag ended:', result);
    if (!result.destination) return;

    const items = Array.from(tasks[selectedDate] || []);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    setTasks(prevTasks => ({
      ...prevTasks,
      [selectedDate]: items
    }));
  };

  return (
    <div className="max-w-4xl mx-auto">
      <h1 className="text-2xl font-semibold mb-6">Daily Tasks</h1>
      <input
        type="date"
        value={selectedDate}
        onChange={(e) => setSelectedDate(e.target.value)}
        className="input mb-4"
      />
      <div className="flex mb-4">
        <input
          placeholder="New task"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          className="input flex-grow mr-2"
        />
        <button onClick={addTask} className="btn flex items-center">
          <PlusCircle className="mr-2 h-4 w-4" /> Add Task
        </button>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="list">
          {(provided) => (
            <div ref={provided.innerRef} {...provided.droppableProps} className="min-h-[100px] border border-dashed border-gray-300 p-4">
              {(tasks[selectedDate] || []).map((task, index) => (
                <Draggable key={task.id} draggableId={task.id} index={index}>
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className={`p-4 mb-2 bg-white rounded shadow ${snapshot.isDragging ? 'shadow-lg' : ''}`}
                    >
                      <div className="flex items-center">
                        <GripVertical className="mr-2 text-gray-400" />
                        <span>{task.title}</span>
                      </div>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default DailyTasks;
