import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { CheckCircle, Circle, Plus, X, ChevronDown, ChevronRight, Edit2, Save } from 'lucide-react';

const WeeklyPriorities = ({ priorities, setPriorities, tasks, setTasks }) => {
  const [addingTaskForPriority, setAddingTaskForPriority] = useState(null);
  const [newTask, setNewTask] = useState({ title: '', estimatedPomodoros: 1 });
  const [expandedTasks, setExpandedTasks] = useState({});
  const [editingTask, setEditingTask] = useState(null);

  const handlePriorityChange = (id, field, value) => {
    setPriorities(priorities =>
      priorities.map(p => p.id === id ? { ...p, [field]: value } : p)
    );
  };

  const getTasksForPriority = (priorityId) => {
    return Object.entries(tasks).flatMap(([date, dailyTasks]) =>
      dailyTasks.filter(task => task.priority === priorityId.toString())
        .map(task => ({ ...task, date }))
    );
  };

  const handleAddTask = (priorityId) => {
    if (newTask.title.trim()) {
      const today = new Date().toISOString().split('T')[0];
      setTasks(prevTasks => ({
        ...prevTasks,
        [today]: [
          ...(prevTasks[today] || []),
          {
            id: Date.now().toString(),
            ...newTask,
            status: 'pending',
            priority: priorityId.toString(),
            date: today
          }
        ]
      }));
      setNewTask({ title: '', estimatedPomodoros: 1 });
      setAddingTaskForPriority(null);
    }
  };

  const toggleTaskExpansion = (taskId) => {
    setExpandedTasks(prev => ({
      ...prev,
      [taskId]: !prev[taskId]
    }));
  };

  const startEditingTask = (task) => {
    setEditingTask({ ...task });
  };

  const saveEditedTask = () => {
    if (editingTask) {
      setTasks(prevTasks => ({
        ...prevTasks,
        [editingTask.date]: prevTasks[editingTask.date].map(task =>
          task.id === editingTask.id ? editingTask : task
        )
      }));
      setEditingTask(null);
    }
  };

  const renderTask = (task) => {
    const isExpanded = expandedTasks[task.id];
    const isEditing = editingTask && editingTask.id === task.id;

    return (
      <div key={task.id} className="mb-2">
        <div className="flex items-center text-sm">
          <button onClick={() => toggleTaskExpansion(task.id)} className="mr-2">
            {isExpanded ? <ChevronDown className="w-4 h-4" /> : <ChevronRight className="w-4 h-4" />}
          </button>
          {task.status === 'done' ? (
            <CheckCircle className="w-4 h-4 text-green-500 mr-2" />
          ) : (
            <Circle className="w-4 h-4 text-gray-300 mr-2" />
          )}
          <span className="flex-grow">{task.title}</span>
          <span className="text-gray-500 ml-2">{task.estimatedPomodoros * 25}m</span>
        </div>
        {isExpanded && (
          <div className="ml-8 mt-2">
            {isEditing ? (
              <>
                <input
                  type="text"
                  value={editingTask.title}
                  onChange={(e) => setEditingTask({ ...editingTask, title: e.target.value })}
                  className="w-full p-1 mb-2 text-sm border rounded"
                />
                <div className="flex items-center">
                  <input
                    type="number"
                    value={editingTask.estimatedPomodoros}
                    onChange={(e) => setEditingTask({ ...editingTask, estimatedPomodoros: parseInt(e.target.value) || 1 })}
                    className="w-16 p-1 text-sm border rounded mr-2"
                    min="1"
                  />
                  <span className="text-sm text-gray-500 mr-2">pomodoros</span>
                  <button onClick={saveEditedTask} className="text-green-500 hover:text-green-700">
                    <Save className="w-4 h-4" />
                  </button>
                </div>
              </>
            ) : (
              <>
                <p className="text-sm text-gray-600 mb-1">{task.estimatedPomodoros} pomodoros</p>
                <button onClick={() => startEditingTask(task)} className="text-blue-500 hover:text-blue-700 text-sm">
                  <Edit2 className="w-4 h-4 inline mr-1" /> Edit
                </button>
              </>
            )}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-semibold mb-6">Weekly Priorities</h1>
      {priorities.map((priority) => (
        <div key={priority.id} className="bg-white rounded-lg shadow-md mb-6 p-4">
          <div className="flex items-center mb-2">
            <span className={`w-3 h-3 rounded-full mr-2 ${priority.type === 'top' ? 'bg-blue-500' : 'bg-blue-300'}`}></span>
            <input
              className="text-lg font-medium bg-transparent border-none focus:outline-none focus:ring-0 w-full"
              placeholder={`Enter ${priority.type === 'top' ? 'top' : 'secondary'} priority`}
              value={priority.title}
              onChange={(e) => handlePriorityChange(priority.id, 'title', e.target.value)}
            />
          </div>
          <textarea
            className="w-full p-2 text-sm bg-gray-50 rounded mb-4 focus:outline-none focus:ring-1 focus:ring-blue-300"
            placeholder="Describe successful outcome"
            value={priority.outcome}
            onChange={(e) => handlePriorityChange(priority.id, 'outcome', e.target.value)}
            rows="2"
          />
          <div className="mt-2">
            {getTasksForPriority(priority.id).map(renderTask)}
            {addingTaskForPriority === priority.id ? (
              <div className="flex items-center mt-2">
                <input
                  type="text"
                  placeholder="New task title"
                  value={newTask.title}
                  onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                  className="flex-grow text-sm p-1 border rounded mr-2"
                />
                <input
                  type="number"
                  value={newTask.estimatedPomodoros}
                  onChange={(e) => setNewTask({ ...newTask, estimatedPomodoros: parseInt(e.target.value) || 1 })}
                  className="w-16 text-sm p-1 border rounded mr-2"
                  min="1"
                />
                <button onClick={() => handleAddTask(priority.id)} className="text-green-500 hover:text-green-700">
                  <Plus className="w-5 h-5" />
                </button>
                <button onClick={() => setAddingTaskForPriority(null)} className="text-red-500 hover:text-red-700 ml-2">
                  <X className="w-5 h-5" />
                </button>
              </div>
            ) : (
              <button 
                onClick={() => setAddingTaskForPriority(priority.id)} 
                className="text-sm text-blue-500 hover:text-blue-700 mt-2 flex items-center"
              >
                <Plus className="w-4 h-4 mr-1" /> Add task
              </button>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default WeeklyPriorities;
