    ];

    setPriorities(initialPriorities);
    setTasks({});
    setBacklogTasks([]);

    localStorage.removeItem('weeklyPriorities');
    localStorage.removeItem('dailyTasks');
    localStorage.removeItem('backlogTasks');

    console.log('State has been reset');
