import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { PlusCircle, CheckCircle2, Clock, XCircle, GripVertical, Edit2, Trash2 } from 'lucide-react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";

// Utility function to get the current date in YYYY-MM-DD format
const getCurrentDate = () => {
  return new Date().toISOString().split('T')[0];
};

// Main App Component
const App = () => {
  const [priorities, setPriorities] = useState(() => {
    const savedPriorities = localStorage.getItem('weeklyPriorities');
    return savedPriorities ? JSON.parse(savedPriorities) : [
      { id: 1, title: '', outcome: '', type: 'top' },
      { id: 2, title: '', outcome: '', type: 'secondary' },
      { id: 3, title: '', outcome: '', type: 'secondary' },
    ];
  });

  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem('dailyTasks');
    return savedTasks ? JSON.parse(savedTasks) : {};
  });

  useEffect(() => {
    localStorage.setItem('weeklyPriorities', JSON.stringify(priorities));
  }, [priorities]);

  useEffect(() => {
    localStorage.setItem('dailyTasks', JSON.stringify(tasks));
  }, [tasks]);

  return (
    <Router>
      <div className="p-4 max-w-4xl mx-auto">
        <nav className="mb-4">
          <ul className="flex space-x-4">
            <li>
              <Link to="/" className="text-blue-500 hover:text-blue-700">Weekly Priorities</Link>
            </li>
            <li>
              <Link to="/daily" className="text-blue-500 hover:text-blue-700">Daily Tasks</Link>
            </li>
            <li>
              <Link to="/analytics" className="text-blue-500 hover:text-blue-700">Analytics</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<WeeklyPriorities priorities={priorities} setPriorities={setPriorities} />} />
          <Route path="/daily" element={<DailyTasks tasks={tasks} setTasks={setTasks} priorities={priorities} />} />
          <Route path="/analytics" element={<Analytics tasks={tasks} />} />
        </Routes>
      </div>
    </Router>
  );
};

// Weekly Priorities Component
const WeeklyPriorities = ({ priorities, setPriorities }) => {
  const handlePriorityChange = (id, field, value) => {
    setPriorities(priorities =>
      priorities.map(p => p.id === id ? { ...p, [field]: value } : p)
    );
  };

  return (
    <Card className="shadow-lg">
      <CardHeader className="bg-gradient-to-r from-blue-500 to-purple-500 text-white">
        <CardTitle className="text-2xl font-bold">Weekly Priorities</CardTitle>
      </CardHeader>
      <CardContent className="mt-4">
        {priorities.map((priority) => (
          <div key={priority.id} className="mb-6">
            <Badge className={`mb-2 ${priority.type === 'top' ? 'bg-yellow-500' : 'bg-blue-500'}`}>
              {priority.type === 'top' ? 'Top Priority' : 'Secondary Priority'}
            </Badge>
            <Input
              placeholder={`Enter ${priority.type === 'top' ? 'top' : 'secondary'} priority`}
              value={priority.title}
              onChange={(e) => handlePriorityChange(priority.id, 'title', e.target.value)}
              className="mb-2"
            />
            <Textarea
              placeholder="Describe successful outcome"
              value={priority.outcome}
              onChange={(e) => handlePriorityChange(priority.id, 'outcome', e.target.value)}
              className="h-24"
            />
          </div>
        ))}
      </CardContent>
    </Card>
  );
};

// Daily Tasks Component
const DailyTasks = ({ tasks, setTasks, priorities }) => {
  const [selectedDate, setSelectedDate] = useState(getCurrentDate());
  const [newTask, setNewTask] = useState({ title: '', estimatedPomodoros: 0, actualPomodoros: 0, status: 'pending', priority: '' });

  const addTask = () => {
    if (newTask.title) {
      setTasks(prevTasks => ({
        ...prevTasks,
        [selectedDate]: [...(prevTasks[selectedDate] || []), { ...newTask, id: Date.now() }]
      }));
      setNewTask({ title: '', estimatedPomodoros: 0, actualPomodoros: 0, status: 'pending', priority: '' });
    }
  };

  const updateTask = (id, updates) => {
    setTasks(prevTasks => ({
      ...prevTasks,
      [selectedDate]: prevTasks[selectedDate].map(t => t.id === id ? { ...t, ...updates } : t)
    }));
  };

  const deleteTask = (id) => {
    setTasks(prevTasks => ({
      ...prevTasks,
      [selectedDate]: prevTasks[selectedDate].filter(t => t.id !== id)
    }));
  };

  const onDragEnd = (result) => {
    if (!result.destination) return;
    const items = Array.from(tasks[selectedDate] || []);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);
    setTasks(prevTasks => ({
      ...prevTasks,
      [selectedDate]: items
    }));
  };

  return (
    <Card className="shadow-lg">
      <CardHeader className="bg-gradient-to-r from-green-500 to-teal-500 text-white">
        <CardTitle className="text-2xl font-bold">Daily Tasks</CardTitle>
      </CardHeader>
      <CardContent>
        <Input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          className="mb-4"
        />
        <div className="flex flex-col sm:flex-row items-end mb-4 gap-2">
          <Input
            placeholder="New task"
            value={newTask.title}
            onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
            className="flex-grow"
          />
          <Input
            type="number"
            placeholder="Est. Pomodoros"
            value={newTask.estimatedPomodoros}
            onChange={(e) => setNewTask({ ...newTask, estimatedPomodoros: parseInt(e.target.value) || 0 })}
            className="w-32"
          />
          <Select onValueChange={(value) => setNewTask({ ...newTask, priority: value })}>
            <SelectTrigger className="w-[180px]">
              <SelectValue placeholder="Select priority" />
            </SelectTrigger>
            <SelectContent>
              {priorities.map((priority) => (
                <SelectItem key={priority.id} value={priority.id.toString()}>
                  {priority.title || `${priority.type === 'top' ? 'Top' : 'Secondary'} Priority ${priority.id}`}
                </SelectItem>
              ))}
              <SelectItem value="unaligned">Unaligned</SelectItem>
            </SelectContent>
          </Select>
          <Button onClick={addTask} className="whitespace-nowrap">
            <PlusCircle className="mr-2 h-4 w-4" /> Add Task
          </Button>
        </div>

        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="tasks">
            {(provided) => (
              <div {...provided.droppableProps} ref={provided.innerRef}>
                {(tasks[selectedDate] || []).map((task, index) => (
                  <Draggable key={task.id} draggableId={task.id.toString()} index={index}>
                    {(provided) => (
                      <Card 
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        className="mb-4 p-4 shadow"
                      >
                        <div className="flex items-center justify-between mb-2">
                          <div {...provided.dragHandleProps} className="mr-2">
                            <GripVertical />
                          </div>
                          <h3 className="text-lg font-semibold flex-grow">{task.title}</h3>
                          <Badge className={
                            task.status === 'done' ? 'bg-green-500' : 
                            task.status === 'defer' ? 'bg-yellow-500' : 
                            task.status === 'delete' ? 'bg-red-500' : 'bg-blue-500'
                          }>
                            {task.status.charAt(0).toUpperCase() + task.status.slice(1)}
                          </Badge>
                        </div>
                        <div className="flex items-center justify-between">
                          <div className="flex items-center">
                            <span className="mr-2">Pomodoros:</span>
                            <Input
                              type="number"
                              value={task.actualPomodoros}
                              onChange={(e) => updateTask(task.id, { actualPomodoros: parseInt(e.target.value) || 0 })}
                              className="w-16 mr-2"
                            />
                            <span>/ {task.estimatedPomodoros}</span>
                          </div>
                          <div className="space-x-2">
                            <Button onClick={() => updateTask(task.id, { status: 'done' })} size="sm" variant="outline">
                              <CheckCircle2 className="mr-1 h-4 w-4" /> Done
                            </Button>
                            <Button onClick={() => updateTask(task.id, { status: 'defer' })} size="sm" variant="outline">
                              <Clock className="mr-1 h-4 w-4" /> Defer
                            </Button>
                            <Dialog>
                              <DialogTrigger asChild>
                                <Button size="sm" variant="outline">
                                  <Edit2 className="mr-1 h-4 w-4" /> Edit
                                </Button>
                              </DialogTrigger>
                              <DialogContent>
                                <DialogHeader>
                                  <DialogTitle>Edit Task</DialogTitle>
                                </DialogHeader>
                                <div className="grid gap-4 py-4">
                                  <div className="grid grid-cols-4 items-center gap-4">
                                    <Label htmlFor="title" className="text-right">
                                      Title
                                    </Label>
                                    <Input
                                      id="title"
                                      value={task.title}
                                      onChange={(e) => updateTask(task.id, { title: e.target.value })}
                                      className="col-span-3"
                                    />
                                  </div>
                                  <div className="grid grid-cols-4 items-center gap-4">
                                    <Label htmlFor="estimatedPomodoros" className="text-right">
                                      Estimated Pomodoros
                                    </Label>
                                    <Input
                                      id="estimatedPomodoros"
                                      type="number"
                                      value={task.estimatedPomodoros}
                                      onChange={(e) => updateTask(task.id, { estimatedPomodoros: parseInt(e.target.value) || 0 })}
                                      className="col-span-3"
                                    />
                                  </div>
                                  <div className="grid grid-cols-4 items-center gap-4">
                                    <Label htmlFor="priority" className="text-right">
                                      Priority
                                    </Label>
                                    <Select
                                      onValueChange={(value) => updateTask(task.id, { priority: value })}
                                      defaultValue={task.priority}
                                    >
                                      <SelectTrigger className="w-[180px]">
                                        <SelectValue placeholder="Select priority" />
                                      </SelectTrigger>
                                      <SelectContent>
                                        {priorities.map((priority) => (
                                          <SelectItem key={priority.id} value={priority.id.toString()}>
                                            {priority.title || `${priority.type === 'top' ? 'Top' : 'Secondary'} Priority ${priority.id}`}
                                          </SelectItem>
                                        ))}
                                        <SelectItem value="unaligned">Unaligned</SelectItem>
                                      </SelectContent>
                                    </Select>
                                  </div>
                                </div>
                              </DialogContent>
                            </Dialog>
                            <Button onClick={() => deleteTask(task.id)} size="sm" variant="outline">
                              <Trash2 className="mr-1 h-4 w-4" /> Delete
                            </Button>
                          </div>
                        </div>
                        <div className="mt-2">
                          <Badge className={
                            task.priority === '1' ? 'bg-yellow-500' :
                            task.priority === '2' || task.priority === '3' ? 'bg-blue-500' :
                            'bg-gray-500'
                          }>
                            {task.priority === '1' ? 'Top Priority' :
                             task.priority === '2' ? 'Secondary Priority 1' :
                             task.priority === '3' ? 'Secondary Priority 2' :
                             'Unaligned'}
                          </Badge>
                        </div>
                      </Card>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      </CardContent>
    </Card>
  );
};

export default App;
