import React, { useState } from 'react';
import { CheckCircle, Circle, Plus, X, Clock, ChevronDown, ChevronRight } from 'lucide-react';

const WeeklyPriorities = ({ priorities, setPriorities, tasks, setTasks }) => {
  const [addingTaskForPriority, setAddingTaskForPriority] = useState(null);
  const [newTask, setNewTask] = useState({ title: '', estimatedPomodoros: 1 });
  const [expandedPriorities, setExpandedPriorities] = useState({});

  const today = new Date().toISOString().split('T')[0];

  const handlePriorityChange = (id, field, value) => {
    setPriorities(priorities =>
      priorities.map(p => p.id === id ? { ...p, [field]: value } : p)
    );
  };

  const getTasksForPriority = (priorityId, dateFilter = null) => {
    return Object.entries(tasks)
      .filter(([date, _]) => !dateFilter || date === dateFilter)
      .flatMap(([date, dailyTasks]) =>
        dailyTasks.filter(task => task.priority === priorityId.toString())
          .map(task => ({ ...task, date }))
      );
  };

  const handleAddTask = (priorityId) => {
    if (newTask.title.trim()) {
      setTasks(prevTasks => ({
        ...prevTasks,
        [today]: [
          ...(prevTasks[today] || []),
          {
            id: Date.now().toString(),
            ...newTask,
            status: 'pending',
            priority: priorityId.toString(),
            date: today
          }
        ]
      }));
      setNewTask({ title: '', estimatedPomodoros: 1 });
      setAddingTaskForPriority(null);
    }
  };

  const handleTaskChange = (taskId, date, field, value) => {
    setTasks(prevTasks => ({
      ...prevTasks,
      [date]: prevTasks[date].map(task =>
        task.id === taskId ? { ...task, [field]: value } : task
      )
    }));
  };

  const toggleTaskStatus = (taskId, date) => {
    setTasks(prevTasks => ({
      ...prevTasks,
      [date]: prevTasks[date].map(task =>
        task.id === taskId ? { ...task, status: task.status === 'done' ? 'pending' : 'done' } : task
      )
    }));
  };

  const togglePriorityExpansion = (priorityId) => {
    setExpandedPriorities(prev => ({
      ...prev,
      [priorityId]: !prev[priorityId]
    }));
  };

  const renderTask = (task) => (
    <div key={task.id} className="flex items-center text-sm mb-2">
      <button onClick={() => toggleTaskStatus(task.id, task.date)} className="mr-2">
        {task.status === 'done' ? (
          <CheckCircle className="w-4 h-4 text-green-500" />
        ) : (
          <Circle className="w-4 h-4 text-gray-300" />
        )}
      </button>
      <input
        value={task.title}
        onChange={(e) => handleTaskChange(task.id, task.date, 'title', e.target.value)}
        className="flex-grow bg-transparent border-none focus:outline-none focus:ring-0"
      />
      <div className="flex items-center ml-2">
        <Clock className="w-4 h-4 text-gray-400 mr-1" />
        <input
          type="number"
          value={task.estimatedPomodoros}
          onChange={(e) => handleTaskChange(task.id, task.date, 'estimatedPomodoros', parseInt(e.target.value) || 1)}
          className="w-12 text-sm p-1 border rounded"
          min="1"
        />
      </div>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-2xl font-semibold mb-6">Weekly Priorities</h1>
      {priorities.map((priority) => {
        const todayTasks = getTasksForPriority(priority.id, today);
        const allTasks = getTasksForPriority(priority.id);
        const isExpanded = expandedPriorities[priority.id];

        return (
          <div key={priority.id} className="bg-white rounded-lg shadow-md mb-6 p-4">
            <div className="flex items-center mb-2">
              <span className={`w-3 h-3 rounded-full mr-2 ${priority.type === 'top' ? 'bg-blue-500' : 'bg-blue-300'}`}></span>
              <input
                className="text-lg font-medium bg-transparent border-none focus:outline-none focus:ring-0 w-full"
                placeholder={`Enter ${priority.type === 'top' ? 'top' : 'secondary'} priority`}
                value={priority.title}
                onChange={(e) => handlePriorityChange(priority.id, 'title', e.target.value)}
              />
            </div>
            <textarea
              className="w-full p-2 text-sm bg-gray-50 rounded mb-4 focus:outline-none focus:ring-1 focus:ring-blue-300"
              placeholder="Describe successful outcome"
              value={priority.outcome}
              onChange={(e) => handlePriorityChange(priority.id, 'outcome', e.target.value)}
              rows="2"
            />
            <div className="mt-2">
              <h3 className="font-medium text-sm mb-2">Today's Tasks:</h3>
              {todayTasks.map(renderTask)}
              {todayTasks.length === 0 && (
                <p className="text-sm text-gray-500 italic">No tasks for today</p>
              )}
              {allTasks.length > todayTasks.length && (
                <button 
                  onClick={() => togglePriorityExpansion(priority.id)}
                  className="text-sm text-blue-500 hover:text-blue-700 mt-2 flex items-center"
                >
                  {isExpanded ? <ChevronDown className="w-4 h-4 mr-1" /> : <ChevronRight className="w-4 h-4 mr-1" />}
                  {isExpanded ? 'Hide' : 'Show'} all tasks ({allTasks.length})
                </button>
              )}
              {isExpanded && allTasks.filter(task => task.date !== today).map(renderTask)}
              {addingTaskForPriority === priority.id ? (
                <div className="flex items-center mt-2">
                  <input
                    type="text"
                    placeholder="New task title"
                    value={newTask.title}
                    onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                    className="flex-grow text-sm p-1 border rounded mr-2"
                  />
                  <input
                    type="number"
                    value={newTask.estimatedPomodoros}
                    onChange={(e) => setNewTask({ ...newTask, estimatedPomodoros: parseInt(e.target.value) || 1 })}
                    className="w-16 text-sm p-1 border rounded mr-2"
                    min="1"
                  />
                  <button onClick={() => handleAddTask(priority.id)} className="text-green-500 hover:text-green-700">
                    <Plus className="w-5 h-5" />
                  </button>
                  <button onClick={() => setAddingTaskForPriority(null)} className="text-red-500 hover:text-red-700 ml-2">
                    <X className="w-5 h-5" />
                  </button>
                </div>
              ) : (
                <button 
                  onClick={() => setAddingTaskForPriority(priority.id)} 
                  className="text-sm text-blue-500 hover:text-blue-700 mt-2 flex items-center"
                >
                  <Plus className="w-4 h-4 mr-1" /> Add task
                </button>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default WeeklyPriorities;
