import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "./components/ui/card";

const Analytics = ({ tasks }) => {
  // Calculate total tasks and completed tasks
  const totalTasks = Object.values(tasks).flat().length;
  const completedTasks = Object.values(tasks).flat().filter(task => task.status === 'done').length;

  // Calculate completion rate
  const completionRate = totalTasks > 0 ? (completedTasks / totalTasks * 100).toFixed(2) : 0;

  return (
    <Card className="shadow-lg">
      <CardHeader className="bg-gradient-to-r from-purple-500 to-pink-500 text-white">
        <CardTitle className="text-2xl font-bold">Analytics</CardTitle>
      </CardHeader>
      <CardContent className="mt-4">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-blue-100 p-4 rounded-lg">
            <h3 className="font-semibold text-lg">Total Tasks</h3>
            <p className="text-3xl font-bold">{totalTasks}</p>
          </div>
          <div className="bg-green-100 p-4 rounded-lg">
            <h3 className="font-semibold text-lg">Completed Tasks</h3>
            <p className="text-3xl font-bold">{completedTasks}</p>
          </div>
          <div className="bg-yellow-100 p-4 rounded-lg">
            <h3 className="font-semibold text-lg">Completion Rate</h3>
            <p className="text-3xl font-bold">{completionRate}%</p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

export default Analytics;
